import{_ as s,a as e,a2 as n,o}from"./chunks/framework.DAlL-BGO.js";const u=JSON.parse('{"title":"","description":"","frontmatter":{"lastUpdated":"2023-12-18 22:22"},"headers":[],"relativePath":"pages/安装手册/Docker安装Nacos.md","filePath":"pages/安装手册/Docker安装Nacos.md","lastUpdated":1733806965000}'),p={name:"pages/安装手册/Docker安装Nacos.md"};function t(c,a,i,l,d,r){return o(),e("div",null,a[0]||(a[0]=[n(`<h2 id="_1-下载" tabindex="-1">1 下载 <a class="header-anchor" href="#_1-下载" aria-label="Permalink to &quot;1 下载&quot;">​</a></h2><p>下载镜像</p><div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>$ docker pull nacos/nacos-server</span></span></code></pre></div><h2 id="_2-启动" tabindex="-1">2 启动 <a class="header-anchor" href="#_2-启动" aria-label="Permalink to &quot;2 启动&quot;">​</a></h2><p>配置文件<code>/opt/nacos/init.d/custom.properties</code>内容如下</p><div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>management.endpoints.web.exposure.include=*</span></span></code></pre></div><p>使用<code>standalone</code>模式并开放<code>8848</code>端口，并映射配置文件和日志目录，数据库默认使用 <code>Derby</code></p><div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>$ docker run -d -p 8848:8848 -e MODE=standalone -v /opt/nacos/init.d/custom.properties:/home/nacos/init.d/custom.properties -v /opt/nacos/logs:/home/nacos/logs --restart always --name nacos nacos/nacos-server</span></span></code></pre></div><h2 id="_3-测试访问" tabindex="-1">3 测试访问 <a class="header-anchor" href="#_3-测试访问" aria-label="Permalink to &quot;3 测试访问&quot;">​</a></h2><p>直接访问 <a href="http://127.0.0.1:8848/nacos%EF%BC%8C" target="_blank" rel="noreferrer">http://127.0.0.1:8848/nacos，</a> 使用账号：nacos，密码：nacos 直接登录。nacos是默认账号和密码，登录成功后可以修改密码。</p><h2 id="_4-使用docker-compose启动" tabindex="-1">4. 使用docker-compose启动 <a class="header-anchor" href="#_4-使用docker-compose启动" aria-label="Permalink to &quot;4. 使用docker-compose启动&quot;">​</a></h2><p>也可以使用 <code>docker-compose</code>方式启动</p><ul><li>配置文件</li></ul><p>docker-compose文件 <code>standalone-derby.yaml</code></p><div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>version: &quot;2&quot;</span></span>
<span class="line"><span>services:</span></span>
<span class="line"><span>  nacos:</span></span>
<span class="line"><span>    image: nacos/nacos-server:latest</span></span>
<span class="line"><span>    container_name: nacos</span></span>
<span class="line"><span>    environment:</span></span>
<span class="line"><span>    - MODE=standalone</span></span>
<span class="line"><span>    volumes:</span></span>
<span class="line"><span>    - /opt/nacos/logs:/home/nacos/logs</span></span>
<span class="line"><span>    -  /opt/nacos/init.d/custom.properties:/home/nacos/init.d/custom.properties</span></span>
<span class="line"><span>    ports:</span></span>
<span class="line"><span>    - &quot;8848:8848&quot;</span></span></code></pre></div><ul><li>启动</li></ul><div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>docker-compose -f standalone-derby.yaml up</span></span></code></pre></div><ul><li>关闭</li></ul><div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>docker-compose -f standalone-derby.yaml stop</span></span></code></pre></div><ul><li>移除</li></ul><div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>docker-compose -f standalone-derby.yaml rm</span></span></code></pre></div><ul><li>关闭且移除</li></ul><div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>docker-compose -f standalone-derby.yaml down</span></span></code></pre></div>`,23)]))}const m=s(p,[["render",t]]);export{u as __pageData,m as default};
